DROP SEQUENCE SHM_SEG_OPCION_SEQ;
CREATE SEQUENCE SHM_SEG_OPCION_SEQ START WITH 1 INCREMENT BY 1 NOCACHE NOCYCLE;

DELETE FROM SHM_SEG_ROL_OPCION;
DELETE SHM_SEG_OPCION;

-- Agregar Insert de Opciones




--- Agregar Insert de Roles para el ADMIN
SELECT ( SELECT ID_ROL FROM SHM_SEG_ROL WHERE CODIGO = 'ADMINISTRADOR_SISTEMA'), ID_OPCION, 1, RAWTOHEX(sys_guid()) , 1 , SYSDATE
FROM SHM_SEG_OPCION
ORDER BY ID_OPCION;

COMMIT;

SELECT * FROM SHM_SEG_OPCION;
